grep -E '^\S+\s+\S+\s+:+\s+search' ./jobExec/log/Worker_* | awk '{print $6}' | sort -u | wc -l
grep -E '^\S+\s+\S+\s+:+\s+search' ./jobExec/log/Worker_* | cut -d' ' -f6- ./jobExec/log/Worker_* | sort -u | awk '{if(NF>1)print $1" "(NF-1)/2}' | awk '{if(!a[$1]){a[$1]=$2}else{a[$1]+=$2}}END{for(i in a){print a[i]" "i}}' | awk '{if(!a[$1]){a[$1]=$2}else{a[$1]=a[$1]","$2}}END{for(i in a){print i" "a[i]}}' | sort -rg | head -1 | awk '{print $2"["$1"]"}'
